$_MODULE['<{alsernetcontents}prestashop>exchangesandreturns_609063fe65e7df8f524ab7087af02e19'] = 'Tierarzneimittel';
$_MODULE['<{alsernetcontents}prestashop>exchangesandreturns_215cb78167328c3321219fffc45a7e40'] = 'Hygienische Masken';
